package Chap02;

public class Varable02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final double PI = 3.14; // 실수형 변수 PI 선언 이때 앞에 final을 붙여 나중에 값이 변하지 않도록 선언
		System.out.print("반지름 3인 원의 넓이를 구합니다   ");// 문자열 "반지름 3인 원의 넓이를 구합니다"를 출력
		System.out.print(3*3*PI); //3*3*PI를 출력

	}

}
