package Chap03;

public class Example01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10, y = 20; // x와y에 10과 20을 입력
		System.out.println("x + y = " + (x+y)); // 문자열 "x+y"를 출력하고 x와 y를 더한 값을 출력
		System.out.println("x - y = " + (x-y)); // 문자열 "x-y"를 출력하고 x에서 y를 뺀 값을 출력
		System.out.println("x * y = " + (x*y)); // 문자열 "x*y"를 출력하고 x와 y를 곱한 값을 출력
		System.out.println("x / y = " + (x/y)); // 문자열 "x/y"를 출력하고 x에서 y를 나눈 값을 출력
		System.out.println("x % y = " + (x%y)); // 문자열 "x%y"를 출력하고 x에서 y를 나누었을 때 나머지 값을 출력

	}

}
