package Chap03;

public class Example05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x =10; // x에 10 입력
		System.out.println(x++); // x를 출력하고 x를 1 증가
		System.out.println(++x); // x를 출력하기 전에 x를 1증가 시키고 x를 출력
		System.out.println(x--); // x를 출력하고 x를 1 감소
		System.out.println(--x); // x를 출력하기 전에 x를 1감소 시키고 x를 출력
		

	}

}
           