package Chap03;

public class Operator02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b = 25;
		System.out.println("a == b " + (a==b));
		// a와 b가 같은지 확인하고 같다면 true, 틀리면 false를 출력
		System.out.println("a != b " + (a!=b));
		// a와 b가 서로 다른지 확인하고 다르면 true, 틀리면 false를 출력
		System.out.println("a > b " + (a>b));
		// a가 b보다 큰지 확인하고 그렇다면 true, 아니라면 false를 출력
		System.out.println("a < b " + (a<b));
		// a가 b보다 작은지 확인하고 그렇다면 true, 아니라면 false를 출력
		System.out.println("a >= b " + (a>=b));
		// a가 b보다 크거나 같은지 확인하고 그렇다면 true, 아니라면 false를 출력
		System.out.println("a <= b " + (a<=b));
		// a가 b보다 작거나 같은지 확인하고 그렇다면 true, 아니라면 false를 출력
	}

}
