package Chap04;

public class Example02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 120;
		
		if(num < 50) {//만약 num이 50보다 작다면
		  System.out.println("숫자가 50보다 작습니다."); //if 안의 문장 실행
		}
		else {
			System.out.println("숫자가 50보다 큽니다."); //그렇지 않다면 이 문장 실행
		}
	}

}
