package Chap04;

public class Example04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 70;
		if(num < 100) {
			System.out.println("숫자가 100보다 작습니다."); //100보다 작을 때 이 코드를 실행
			if(num > 50) {
				System.out.println("숫자가 50보다 큽니다"); // 100보다 작고 50 보다 클 때 이 코드를 실행
			}
		}
	}

}
